import { LitElement, html } from 'lit-element';
import { CellsResizableMixin as cellsResizableMixin } from '../../cells-resizable-mixin.js';
import './x-puck.js';

class XApp extends cellsResizableMixin(LitElement) {
  static get is() {
    return 'x-app';
  }
  render() {
    return html`
      <style>
        :host {
          display: block;
          position: absolute;
          top: 0;
          right: 0;
          bottom: 0;
          left: 0;
        }
      </style>
      <x-puck></x-puck>
    `;
  }
}
customElements.define(XApp.is, XApp);
