import { LitElement, html, } from 'lit-element';
import { getComponentSharedStyles, } from '@cells-components/cells-lit-helpers/cells-lit-helpers.js';
import '@cells-components/cells-template-paper-drawer-panel/cells-template-paper-drawer-panel.js';

class FakePage extends LitElement {
  static get is() {
    return 'fake-page';
  }
  render() {
    return html`
      <style>
        :host {
          font-family: sans-serif;
          overflow-x: hidden;
        }

        cells-template-paper-drawer-panel {
          --cells-template-paper-drawer-panel-section-bg: #237ABA;
          background: #237ABA;
        }

        .btn {
          font: inherit;
          font-size: 15px;
          color: #fff;
          background-color: #004481;
          border-radius: 1px;
          border: 0;
          padding: 15px 30px;
          min-width: 180px;
          cursor: pointer;
          transform: translate(40px, 20px);
        }
      </style>

      <cells-template-paper-drawer-panel animation-type="horizontal" mode="seamed">
        <div slot="app__main" data-select="app__main">
          <button class="btn" @click="${this.goTo}">Back to page 1</button>
        </div>
      </cells-template-paper-drawer-panel>
    `;
  }
  goTo() {
    this.dispatchEvent(new CustomEvent('button-click'));
  }
}
window.customElements.define(FakePage.is, FakePage);
